sys_variable_value.value:
(function execute(inputs, outputs) {
	var server = new GlideRecord("cmdb_ci_server");
	server.addQuery("sys_id", inputs.ci);
	server.query();
  	while(server.next()){
      var servername = server.getValue('name');
    }
    var gr = new GlideRecord('sn_agent_ci_extended_info');
	gr.addQuery('name','CONTAINS',servername);
	gr.query();
	while (gr.next()) {
  		var state = gr.getValue('status');
    }
  	if(state == 2)
      throw new Error("ACC "+servername+" is down");
	var cis = new GlideRecord("cmdb_ci_server");
	cis.addQuery("sys_id", CISysId);
	cis.query();
	check = {
		"checkDefId": "55bef782db3420102266187d139619a6",
		"params": {
			"command": inputs.command
		}
	};
	var agentNow = new sn_agent.AgentNowHandler();
	var reqId = agentNow.runCheckForCis(cis, check, '1', inputs.timeout);
	outputs.request_id = reqId;
})(inputs, outputs);

