sys_variable_value.value:
(function execute(inputs, outputs) {
	var gr = new GlideRecord('x_snc_agent_client_action_response');
	gr.addQuery('request_id', inputs.request_id);
	gr.query();
	while (gr.next()) {
		var state = gr.getValue('state');
		if (state = 'done') {
			var response = JSON.parse(gr.getValue('response'));
			if (response.check.status == 0) {
				outputs.status = "Success";
				outputs.response = response.check.output;
			} else {
				outputs.status = "Error";
				var message = response.check.output;
				outputs.error_message = message.slice(0,message.indexOf('['));
			}
		}
	}
})(inputs, outputs);

