							status = grInfo.getValue('status');
							sysId = grInfo.getValue('cmdb_ci'); //agent host id from cmdb agent name
						}
						if (status == 2)
							throw new Error("Agent " + name + " running on provied CI " + inputs.ci + " is down, Restart the agent.");
					} else {
						throw new Error("Provided CI does not have any agent installed.");
					}
				}

			}
		} else {
			var ciId = new GlideRecord('cmdb_ci');
			ciId.addQuery("sys_id", inputs.ci);
			ciId.query();
			while (ciId.next()) {
				ciName = ciId.getValue('name');
				ciSysId = ciId.getValue('sys_id');
				if (ciName.startsWith("Agent_")) {
					var gr = new GlideRecord('sn_agent_ci_extended_info');
					gr.addQuery('name', ciName);
					gr.query();
					while (gr.next()) {
						status = gr.getValue('status');
						sysId = gr.getValue('cmdb_ci');
					}
					if (status == 2)
						throw new Error("ACC " + ciName + " is down, Restart the agent.");
				} else {
					var grInfo = new GlideRecord('sn_agent_ci_extended_info');
					grInfo.addQuery('cmdb_ci', ciSysId);
					grInfo.query();
					if (grInfo.hasNext()) {
						while (grInfo.next()) {
							status = grInfo.getValue('status');
							sysId = grInfo.getValue('cmdb_ci');
						}
						if (status == 2)
							throw new Error("Agent " + name + " running on provied CI " + inputs.ci + " is down, Restart the agent.");
					} else {
						throw new Error("Provided CI does not have any agent installed.");
					}
				}

			}
		}

	}
	if (sysId == null) {
		throw new Error("Invalid CI");
	}
	var cis = new GlideRecord("cmdb_ci_server");
	cis.addQuery("sys_id", sysId);
	cis.query();
  	var command;
  	if(inputs.command)
  		command = inputs.command;
    if(inputs.look_up_command){
        var grQuery = new GlideRecord('sn_pb_content_query_definition');
      	grQuery.addQuery('name',inputs.look_up_command);
      	grQuery.query();
      	while(grQuery.next()){
          osquery = grQuery.getValue('command');
          var supportedOs = grQuery.getValue('supported_os');
        }  
    }
	check = {
		"checkDefId": "55bef782db3420102266187d139619a6",
		"params": {
			"command": command
		}
	};
	var agentNow = new sn_agent.AgentNowHandler();
	var reqId = agentNow.runCheckForCis(cis, check, '1', inputs.timeout);
	outputs.request_id = reqId;
})(inputs, outputs);

