sys_variable_value.value:
(function execute(inputs, outputs) {
	var gr = new GlideRecord('sn_agent_ci_extended_info');
	var name = "Agent_" + inputs.agent;
	gr.addQuery('name', name);
	gr.query();
	while (gr.next()) {
		var status = gr.getValue('status');
		if (status == 1)
			outputs.status = "warning";
		if (status == 2)
			outputs.status = "Down";
		if (status == 3)
			outputs.status = "Restarting";
		if (status == 0)
			outputs.status = "Up";
	}
})(inputs, outputs);

